// Read Altitude from barometric sensor, note that the sensor is 1m accurate
double {{varname}}Alt = {{varname}}.altitude();
double {{varname}}Pressure = {{varname}}.getPressure();
double {{varname}}TempC = {{varname}}.getTemperatureC();     //See also {{varname}}.getTemperatureF() for Fahrenheit
Serial.print(F("Altitude: ")); Serial.print({{varname}}Alt,1); Serial.print(F(" [m]"));
Serial.print(F("\tpressure: ")); Serial.print({{varname}}Pressure,1); Serial.print(F(" [hPa]"));
Serial.print(F("\tTemperature: ")); Serial.print({{varname}}TempC,1); Serial.println(F(" [°C]"));
