for(int i=0 ; i <= NUMOFPIXEL ; i++){
        // set pixels Color to RGB values, from 0,0,0 up to 255,255,255
        {{varname}}.setPixelColor(i, {{varname}}.Color(0,150,0)); // turn on green color on led #i.
        if (i > 0)
                {{varname}}.setPixelColor(i-1, {{varname}}.Color(0,0,0)); // turn off last led
        {{varname}}.show(); //update pixel color to the hardware.
        delay(50);
}
