uint16_t x, y;
uint8_t z;
if ({{varname}}.touched()) {
  // read x & y & z;
  while (! {{varname}}.bufferEmpty()) {
    Serial.print({{varname}}.bufferSize());
    {{varname}}.readData(&x, &y, &z);
    Serial.print("->(");
    Serial.print(x); Serial.print(", ");
    Serial.print(y); Serial.print(", ");
    Serial.print(z);
    Serial.println(")");
  }
  {{varname}}.writeRegister8(STMPE_INT_STA, 0xFF); // reset all ints
}
delay(10);
