code.snippetCode:
//This loop will turn on and off each segment in the array for 0.5 sec
for (int i = 0; i < 8; i++) { 
  digitalWrite({{varname}}Pins[i],LOW);
  delay(500);
}
for (int i = 0; i < 8; i++) { 
  digitalWrite({{varname}}Pins[i],HIGH);
}
